import{_ as e,c as t,o,a as c}from"./app.0a6056ed.js";const m=JSON.parse('{"title":"Acts of the Council","description":"","frontmatter":{},"headers":[{"level":2,"title":"Decisions of the Precursor Council - CivTemp and Devoted","slug":"decisions-of-the-precursor-council-civtemp-and-devoted"},{"level":3,"title":"Council Act 1.1","slug":"council-act-1-1"},{"level":3,"title":"Council Act 1.2","slug":"council-act-1-2"},{"level":3,"title":"Council Act 1.3","slug":"council-act-1-3"},{"level":3,"title":"Council Act 1.4","slug":"council-act-1-4"},{"level":3,"title":"Council Act 1.5","slug":"council-act-1-5"},{"level":3,"title":"Council Act 1.6","slug":"council-act-1-6"},{"level":2,"title":"Decisions of the First Council - August 14th 2017 to October 14th 2017","slug":"decisions-of-the-first-council-august-14th-2017-to-october-14th-2017"},{"level":3,"title":"Council Act 1.7","slug":"council-act-1-7"},{"level":3,"title":"Council Act 1.8","slug":"council-act-1-8"},{"level":3,"title":"Council Act 4.0","slug":"council-act-4-0"},{"level":3,"title":"Council Act 4.1","slug":"council-act-4-1"},{"level":3,"title":"Council Act 5.1","slug":"council-act-5-1"},{"level":3,"title":"Council Act 5.2","slug":"council-act-5-2"},{"level":3,"title":"Council Act 5.3","slug":"council-act-5-3"},{"level":3,"title":"Council Act 5.4","slug":"council-act-5-4"},{"level":3,"title":"Council Act 5.5","slug":"council-act-5-5"},{"level":3,"title":"Council Act 5.6","slug":"council-act-5-6"},{"level":3,"title":"Council Act 5.7","slug":"council-act-5-7"},{"level":3,"title":"Council Act 5.8","slug":"council-act-5-8"},{"level":3,"title":"Council Act 5.9","slug":"council-act-5-9"},{"level":3,"title":"Council Act 5.10","slug":"council-act-5-10"},{"level":3,"title":"Council Act 5.11","slug":"council-act-5-11"},{"level":2,"title":"Decisions of the Second Council - October 14th, 2017 to December 14th, 2017","slug":"decisions-of-the-second-council-october-14th-2017-to-december-14th-2017"},{"level":3,"title":"Council Act 6.1","slug":"council-act-6-1"},{"level":3,"title":"Council Act 6.2","slug":"council-act-6-2"},{"level":3,"title":"Council Act 6.3","slug":"council-act-6-3"},{"level":3,"title":"Council Act 6.4","slug":"council-act-6-4"},{"level":3,"title":"Council Act 6.5","slug":"council-act-6-5"},{"level":3,"title":"Council Act 6.6","slug":"council-act-6-6"},{"level":3,"title":"Council Act 6.7","slug":"council-act-6-7"},{"level":3,"title":"Council Act 6.8","slug":"council-act-6-8"},{"level":3,"title":"Council Act 7.1","slug":"council-act-7-1"},{"level":3,"title":"Council Act 8.1","slug":"council-act-8-1"},{"level":3,"title":"Council Act 8.2","slug":"council-act-8-2"},{"level":3,"title":"Council Act 8.3","slug":"council-act-8-3"},{"level":2,"title":"Decisions of the Third Council - December 14th, 2017 to February 14th, 2018","slug":"decisions-of-the-third-council-december-14th-2017-to-february-14th-2018"},{"level":3,"title":"Council Act 9.1","slug":"council-act-9-1"},{"level":3,"title":"Council Act 9.2","slug":"council-act-9-2"},{"level":3,"title":"Council Act 9.3","slug":"council-act-9-3"},{"level":3,"title":"Council Act 9.4","slug":"council-act-9-4"},{"level":3,"title":"Council Act 9.5","slug":"council-act-9-5"},{"level":3,"title":"Council Act 9.6","slug":"council-act-9-6"},{"level":3,"title":"Council Act 9.7","slug":"council-act-9-7"}],"relativePath":"government/council-acts.md","lastUpdated":1660784139000}'),a={name:"government/council-acts.md"},i=c('<h1 id="acts-of-the-council" tabindex="-1">Acts of the Council <a class="header-anchor" href="#acts-of-the-council" aria-hidden="true">#</a></h1><p><em>(Amendments are hidden by spoiler text by default to avoid confusion; Acts (or parts of Acts) which have been repealed or superceded are crossed out)</em></p><nav class="table-of-contents"><ul><li><a href="#decisions-of-the-precursor-council-civtemp-and-devoted">Decisions of the Precursor Council - CivTemp and Devoted</a><ul><li><a href="#council-act-1-1">Council Act 1.1</a></li><li><a href="#council-act-1-2">Council Act 1.2</a></li><li><a href="#council-act-1-3">Council Act 1.3</a></li><li><a href="#council-act-1-4">Council Act 1.4</a></li><li><a href="#council-act-1-5">Council Act 1.5</a></li><li><a href="#council-act-1-6">Council Act 1.6</a></li></ul></li><li><a href="#decisions-of-the-first-council-august-14th-2017-to-october-14th-2017">Decisions of the First Council - August 14th 2017 to October 14th 2017</a><ul><li><a href="#council-act-1-7">Council Act 1.7</a></li><li><a href="#council-act-1-8">Council Act 1.8</a></li><li><a href="#council-act-4-0">Council Act 4.0</a></li><li><a href="#council-act-4-1">Council Act 4.1</a></li><li><a href="#council-act-5-1">Council Act 5.1</a></li><li><a href="#council-act-5-2">Council Act 5.2</a></li><li><a href="#council-act-5-3">Council Act 5.3</a></li><li><a href="#council-act-5-4">Council Act 5.4</a></li><li><a href="#council-act-5-5">Council Act 5.5</a></li><li><a href="#council-act-5-6">Council Act 5.6</a></li><li><a href="#council-act-5-7">Council Act 5.7</a></li><li><a href="#council-act-5-8">Council Act 5.8</a></li><li><a href="#council-act-5-9">Council Act 5.9</a></li><li><a href="#council-act-5-10">Council Act 5.10</a></li><li><a href="#council-act-5-11">Council Act 5.11</a></li></ul></li><li><a href="#decisions-of-the-second-council-october-14th-2017-to-december-14th-2017">Decisions of the Second Council - October 14th, 2017 to December 14th, 2017</a><ul><li><a href="#council-act-6-1">Council Act 6.1</a></li><li><a href="#council-act-6-2">Council Act 6.2</a></li><li><a href="#council-act-6-3">Council Act 6.3</a></li><li><a href="#council-act-6-4">Council Act 6.4</a></li><li><a href="#council-act-6-5">Council Act 6.5</a></li><li><a href="#council-act-6-6">Council Act 6.6</a></li><li><a href="#council-act-6-7">Council Act 6.7</a></li><li><a href="#council-act-6-8">Council Act 6.8</a></li><li><a href="#council-act-7-1">Council Act 7.1</a></li><li><a href="#council-act-8-1">Council Act 8.1</a></li><li><a href="#council-act-8-2">Council Act 8.2</a></li><li><a href="#council-act-8-3">Council Act 8.3</a></li></ul></li><li><a href="#decisions-of-the-third-council-december-14th-2017-to-february-14th-2018">Decisions of the Third Council - December 14th, 2017 to February 14th, 2018</a><ul><li><a href="#council-act-9-1">Council Act 9.1</a></li><li><a href="#council-act-9-2">Council Act 9.2</a></li><li><a href="#council-act-9-3">Council Act 9.3</a></li><li><a href="#council-act-9-4">Council Act 9.4</a></li><li><a href="#council-act-9-5">Council Act 9.5</a></li><li><a href="#council-act-9-6">Council Act 9.6</a></li><li><a href="#council-act-9-7">Council Act 9.7</a></li></ul></li></ul></nav><h2 id="decisions-of-the-precursor-council-civtemp-and-devoted" tabindex="-1">Decisions of the Precursor Council - CivTemp and Devoted <a class="header-anchor" href="#decisions-of-the-precursor-council-civtemp-and-devoted" aria-hidden="true">#</a></h2><h3 id="council-act-1-1" tabindex="-1">Council Act 1.1 <a class="header-anchor" href="#council-act-1-1" aria-hidden="true">#</a></h3><blockquote><p><s>Individual judges order their courts how they see fit, within reason; a player can appeal the decision to the alcuahtl if they believe a mistrial has taken place; criminal trials have to go to court but civil trials can be decided via trial by carrot or by arbitration of a judge willing to hear the case.</s></p></blockquote><p><em>(Repealed via <a href="#council-act-4-0">Council Act 4.0</a>)</em></p><h3 id="council-act-1-2" tabindex="-1">Council Act 1.2 <a class="header-anchor" href="#council-act-1-2" aria-hidden="true">#</a></h3><blockquote><p><s>The Alcuahtl\u2019s decision on a court case is final, no further appeals shall be allowed.</s></p></blockquote><p><em>(Repealed via <a href="#council-act-4-0">Council Act 4.0</a>)</em></p><h3 id="council-act-1-3" tabindex="-1">Council Act 1.3 <a class="header-anchor" href="#council-act-1-3" aria-hidden="true">#</a></h3><blockquote><p><s>Cities should have documented evidence of their consent to joining Yoahtl, and should undergo a trial period as a protectorate before officially joining the Empire to assess their activity level and suitableness for the community; all prospective cities, founded within or outside Yoahtl, must submit a charter outlining their government form, how their senator will be chosen, and why they should be a separate town within Yoahtl. To become a full city, they should also meet the minimum population requirement (5 actives minimum, as determined by the state of reinforcement decay on structures in their town).</s></p></blockquote><p><em>(Repealed via <a href="#council-act-6-1">Council Act 6.1</a>)</em></p><h3 id="council-act-1-4" tabindex="-1">Council Act 1.4 <a class="header-anchor" href="#council-act-1-4" aria-hidden="true">#</a></h3><blockquote><p>Any group of players wishing to form a town may submit papers of incorporation to the Council of Yoahtl, which are then voted on and the Alcuahtl must sign off. A town may choose to be autonomous, which means that they retain their own legal structure and government. They also are exempt from participating in warfare unless they choose to contribute. They are, however, unable to vote for the Council and choose their own foreign relations except in the realm of trade. Non-autonomous towns retain the Yoahtlan legal code and can vote for the Council. They also cannot form foreign relations. All towns retain the rights to any developed settlements (excludes farms, roads and other minor infrastructure) in the case of inactivity. Existing towns may join Yoahtl as an automatically incorporated town and may choose autonomy at their discretion. Resimere and Pacem retain the rights to all their land in the case of inactivity as per their agreements upon joining Yoahtl.</p></blockquote><p><em>(Amended via <a href="#council-act-6.1">Council Act 6.1</a>; Amended via <a href="#council-act-6-4">Council Act 6.4</a>; Amended via <a href="#council-act-9.6">Council Act 9.6</a>; Amended via <a href="#council-act-13.3">Council Act 13.3</a>; Amended via <a href="#council-act-15.2">Council Act 15.2</a>; Amended via Ololtic Proposal 8)</em></p><h3 id="council-act-1-5" tabindex="-1">Council Act 1.5 <a class="header-anchor" href="#council-act-1-5" aria-hidden="true">#</a></h3><blockquote><p><s>The Yoahtlan council is empowered to dispatch an appointed deputy (&quot;meter-reader&quot;) to check the state of house reinforcements within the town. If a town fails to meet the minimum activity requirement, they will be put on probation for a month and their senator will not be required to achieve a quorum for senate votes; if after a month they have not achieved minimum activity again, their senate seat will be dissolved.</s></p></blockquote><p><em>(Repealed via <a href="#council-act-4-0">Council Act 4.0</a>)</em></p><h3 id="council-act-1-6" tabindex="-1">Council Act 1.6 <a class="header-anchor" href="#council-act-1-6" aria-hidden="true">#</a></h3><blockquote><p><s>In order to run for council, a candidate (freshman or incumbent) must pay 20d to the national treasury to get on the ballot; this will serve as a fundraiser for national construction projects and other government programs, and (hopefully) give the candidate a sense of investment in the success of the Empire as a whole. The buy-in may be paid either by the candidate themself or by a donor individual or organization.</s></p></blockquote><p><em>(Repealed via <a href="#council-act-5-7">Council Act 5.7</a>)</em></p><h2 id="decisions-of-the-first-council-august-14th-2017-to-october-14th-2017" tabindex="-1">Decisions of the First Council - August 14th 2017 to October 14th 2017 <a class="header-anchor" href="#decisions-of-the-first-council-august-14th-2017-to-october-14th-2017" aria-hidden="true">#</a></h2><h3 id="council-act-1-7" tabindex="-1">Council Act 1.7 <a class="header-anchor" href="#council-act-1-7" aria-hidden="true">#</a></h3><blockquote><p>A citizen must register at least one week ahead of time to vote in Council elections, or be eligible to run for Council.</p></blockquote><p><em>(Repealed via <a href="#council-act-13-3">Council Act 13.3</a>; Restored via <a href="#council-act-13-8">Council Act 13.8</a>)</em></p><h3 id="council-act-1-8" tabindex="-1">Council Act 1.8 <a class="header-anchor" href="#council-act-1-8" aria-hidden="true">#</a></h3><blockquote><p>Whether to allow or disallow observation of council proceedings by Yoahtlan discord members will be decided at the beginning of each meeting.</p></blockquote><h3 id="council-act-4-0" tabindex="-1">Council Act 4.0 <a class="header-anchor" href="#council-act-4-0" aria-hidden="true">#</a></h3><blockquote><p>Approve the Third Charter.</p></blockquote><blockquote><p>Repeal Council Acts <a href="#council-act-1-1">1.1</a>, <a href="#council-act-1-2">1.2</a>, and <a href="#council-act-1-5">1.5</a>.</p></blockquote><p><em>(Amended via <a href="#council-act-5-7">Council Act 5.7</a>; Amended via <a href="#council-act-13-6">Council Act 13.6</a>; Amended via <a href="#council-act-13-8">Council Act 13.8</a>)</em></p><h3 id="council-act-4-1" tabindex="-1">Council Act 4.1 <a class="header-anchor" href="#council-act-4-1" aria-hidden="true">#</a></h3><blockquote><p>Residency may be granted at any Government member&#39;s discretion on the basis of residing partly or wholly in Yoahtl and trust.</p></blockquote><p><em>(Amended via <a href="#council-act-6-3">Council Act 6.3</a>; Amended via <a href="#council-act-6-8">Council Act 6.8</a>; Amended via <a href="#council-act-10-5">Council Act 10.5</a>; Repealed via <a href="#council-act-13-3">Council Act 13.3</a>; Restored via <a href="#council-act-13-8">Council Act 13.8</a>; Amended via <a href="#council-act-13-8">Council Act 13.8</a>)</em></p><h3 id="council-act-5-1" tabindex="-1">Council Act 5.1 <a class="header-anchor" href="#council-act-5-1" aria-hidden="true">#</a></h3><blockquote><p>All undeveloped and derelict property is owned by the Government. Plots/land are to be distributed solely by Government members, for free or a fee as deemed necessary. Property transactions between private parties must have prior approval from the Government.</p></blockquote><p><em>(Amended via <a href="#council-act-9-4">Council Act 9.4</a>)</em></p><h3 id="council-act-5-2" tabindex="-1">Council Act 5.2 <a class="header-anchor" href="#council-act-5-2" aria-hidden="true">#</a></h3><blockquote><p>Construction outside the city walls requires pre-approval from a Government member. This rule does not apply to non-autonomous towns.</p></blockquote><p><em>(Amended via <a href="#council-act-15-8">Council Act 15.8</a>)</em></p><h3 id="council-act-5-3" tabindex="-1">Council Act 5.3 <a class="header-anchor" href="#council-act-5-3" aria-hidden="true">#</a></h3><blockquote><p>What is criminal is established primarily through the arbitration of disputes by the courts, and secondarily through specific lines of council law.</p></blockquote><h3 id="council-act-5-4" tabindex="-1">Council Act 5.4 <a class="header-anchor" href="#council-act-5-4" aria-hidden="true">#</a></h3><blockquote><p>Unimproved spawners are considered public property and breaking them without government approval is considered a crime.</p></blockquote><h3 id="council-act-5-5" tabindex="-1">Council Act 5.5 <a class="header-anchor" href="#council-act-5-5" aria-hidden="true">#</a></h3><blockquote><p><s><strong>Executive</strong> Create a new district to the North of the settlement for newfriends with: roads suggesting plots, but not defining them; a board where we can post jobs for newfriends to carry out; and apartment buildings for newfriends to stay at with factory rooms.</s></p></blockquote><p><em>(Retired via <a href="#council-act-24-2">Council Act 24.2</a>)</em></p><h3 id="council-act-5-6" tabindex="-1">Council Act 5.6 <a class="header-anchor" href="#council-act-5-6" aria-hidden="true">#</a></h3><blockquote><p><s><strong>Executive</strong> Claim mushroom island at -2233, 5731.</s></p></blockquote><p><em>(Retired via <a href="#council-act-24-2">Council Act 24.2</a>)</em></p><h3 id="council-act-5-7" tabindex="-1">Council Act 5.7 <a class="header-anchor" href="#council-act-5-7" aria-hidden="true">#</a></h3><blockquote><p><strong>Executive</strong> Officially adopt new group structure.</p></blockquote><blockquote><p>Repeal Council Act <a href="#council-act-1-6">1.6</a>.</p></blockquote><h3 id="council-act-5-8" tabindex="-1">Council Act 5.8 <a class="header-anchor" href="#council-act-5-8" aria-hidden="true">#</a></h3><blockquote><p>Councillors that are absent from a meeting will count as abstaining on any votes unless they provide both 24 hours notice and an alternate meeting time.</p></blockquote><p><em>(Repealed via <a href="#council-act-13-3">Council Act 13.3</a>; Restored via <a href="#council-act-13-8">Council Act 13.8</a>)</em></p><h3 id="council-act-5-9" tabindex="-1">Council Act 5.9 <a class="header-anchor" href="#council-act-5-9" aria-hidden="true">#</a></h3><blockquote><p><strong>Executive</strong> Revoke Wacky_Aki&#39;s directorship of Akiharambe District.</p></blockquote><h3 id="council-act-5-10" tabindex="-1">Council Act 5.10 <a class="header-anchor" href="#council-act-5-10" aria-hidden="true">#</a></h3><blockquote><p><s>Dereliction: A building will pass to a new owner if they place a dereliction notice in an obvious position with proof, the original owner does not remove it within two weeks, or has previously warned of their extended absence, and the transfer is given approval by a government member.</s></p></blockquote><p><em>(Repealed via <a href="#council-act-6-2">Council Act 6.2</a>)</em></p><h3 id="council-act-5-11" tabindex="-1">Council Act 5.11 <a class="header-anchor" href="#council-act-5-11" aria-hidden="true">#</a></h3><blockquote><p>The construction of death traps without two-third&#39;s Council approval is forbidden.</p></blockquote><h2 id="decisions-of-the-second-council-october-14th-2017-to-december-14th-2017" tabindex="-1">Decisions of the Second Council - October 14th, 2017 to December 14th, 2017 <a class="header-anchor" href="#decisions-of-the-second-council-october-14th-2017-to-december-14th-2017" aria-hidden="true">#</a></h2><h3 id="council-act-6-1" tabindex="-1">Council Act 6.1 <a class="header-anchor" href="#council-act-6-1" aria-hidden="true">#</a></h3><blockquote><p>Repeal Council Act 1.3.</p></blockquote><blockquote><p>Amend Council Act 1.4:</p><details class="details custom-block"><summary>Details</summary><p>A protectorate is a city <strong>or region designated by the Council and</strong> under the protection of <s>Yoahtl; an</s> <strong>Yoahtl. An</strong> attack on it is an attack on the Empire of <s>Yoahtl itself. However, the city is not required to submit a charter for council approval, does not have a senator, and citizens of protectorates are ineligible to run for council or vote.</s> <strong>Yoahtl. Citizens of protectorates are considered Yoahtlan residends and ineligible to run for council or vote. A protectorate is required to hold regular elections to elect a Governor to act as the representative of the Yoahtlan State. A governor is considered to be a citizen of Yoahtl and may cast votes on behalf of one-third of the protectorate\u2019s citizens. Protectorates are required to contribute men and materiel during war-time. A vassal is a city or region designated by the Council and under the protection of Yoahtl. An attack on it is an attack on the Empire of Yoahtl. Vassals retain their own governing and legal structure, exempting them from article Article V of the Charter. They must submit to Yoahtlan law in cases involving Yoahtlan citizens, and must adhere to Yoahtlan foreign policy. Vassals are required to contribute men and materiel during war-time.</strong></p></details></blockquote><p><em>(Partly superceded via <a href="#council-act-6-4">Council Act 6.4</a>)</em></p><h3 id="council-act-6-2" tabindex="-1">Council Act 6.2 <a class="header-anchor" href="#council-act-6-2" aria-hidden="true">#</a></h3><blockquote><p>The Alcuahtl assigns dereliction duty to an individual councillor. This duty cannot be delegated. The longest-inactive players are the first available for eviction and dereliction. The Derelictor and Alcuahtl have discretion over the need to derelict, but cannot abuse the power. The Council may force the Alcuahtl to reconsider their choice of derelictor, but may not force a resignation without proof of wrongdoing.</p></blockquote><blockquote><p>Repeal Council Act 5.10.</p></blockquote><p><em>(Amended via <a href="#council-act-13-3">Council Act 13.3</a>; Amended via <a href="#council-act-13-8">Council Act 13.8</a>; Amended via <a href="#council-act-15-6">Council Act 15.6</a>)</em></p><h3 id="council-act-6-3" tabindex="-1">Council Act 6.3 <a class="header-anchor" href="#council-act-6-3" aria-hidden="true">#</a></h3><blockquote><p><s>Amend Council Act 4.1:</s></p><details class="details custom-block"><summary>Details</summary><p>Citizenship in Yoahtl is determined by residency in a Yoahtlan town or on Yoahtlan territory. <strong>Citizenship may be granted at the Alcuahtl or Deputy&#39;s discretion on the basis of trust and significant contributions.</strong></p></details></blockquote><p><em>(Superceded via <a href="#council-act-6-8">Council Act 6.8</a>)</em></p><h3 id="council-act-6-4" tabindex="-1">Council Act 6.4 <a class="header-anchor" href="#council-act-6-4" aria-hidden="true">#</a></h3><blockquote><p><s>Amend Council Act 1.4:</s></p><details class="details custom-block"><summary>Details</summary><p>A protectorate is a city or region designated by the Council and under the protection of Yoahtl. An attack on it is an attack on the Empire of Yoahtl. Citizens of protectorates are considered Yoahtlan residends and ineligible to run for council or vote. A protectorate is required to hold regular elections to elect a Governor to act as the representative of the Yoahtlan State. A governor is considered to be a citizen of Yoahtl and may cast votes on behalf of one-third of the protectorate\u2019s citizens. Protectorates are required to contribute men and materiel during war-time. <strong>The Alcuahtl may appoint a governor to administer the protectorate at their discretion.</strong> A vassal is a city or region designated by the Council and under the protection of Yoahtl. An attack on it is an attack on the Empire of Yoahtl. Vassals retain their own governing and legal structure, exempting them from article Article V of the Charter. They must submit to Yoahtlan law in cases involving Yoahtlan citizens, and must adhere to Yoahtlan foreign policy. Vassals are required to contribute men and materiel during war-time.</p></details></blockquote><p><em>(Superceded via <a href="#council-act-9-6">Council Act 9.6</a>)</em></p><h3 id="council-act-6-5" tabindex="-1">Council Act 6.5 <a class="header-anchor" href="#council-act-6-5" aria-hidden="true">#</a></h3><blockquote><p>Dual citizens who do not have primary residence in Yoahtl or hold office in any other nation are ineligible to run for Council.</p></blockquote><p><em>(Repealed via <a href="#council-act-13-3">Council Act 13.3</a>; Restored via <a href="#council-act-13-8">Council Act 13.8</a>)</em></p><h3 id="council-act-6-6" tabindex="-1">Council Act 6.6 <a class="header-anchor" href="#council-act-6-6" aria-hidden="true">#</a></h3><blockquote><p><s>Dual citizens of protectorates and Yoahtl must chose in registration whether they will vote personally or have their voted counted towards their Governor&#39;s votes.</s></p></blockquote><p><em>(Repealed via <a href="#council-act-9-7">Council Act 9.7</a>)</em></p><h3 id="council-act-6-7" tabindex="-1">Council Act 6.7 <a class="header-anchor" href="#council-act-6-7" aria-hidden="true">#</a></h3><blockquote><p>Citizenship may be suspended on the grounds of inactivity.</p></blockquote><blockquote><p>If a person, whose citizenship is suspended, has become active again and has shown the intent to continue to be active then they may request that their citizenship is unsuspended. This request will then be granted by the alcuahtl or a councillor.</p></blockquote><p><em>(Amended via [Proposal] Suspended Citizenship Restoration Procedure)</em></p><h3 id="council-act-6-8" tabindex="-1">Council Act 6.8 <a class="header-anchor" href="#council-act-6-8" aria-hidden="true">#</a></h3><blockquote><p><s>Amend Council Act 4.1:</s></p><details class="details custom-block"><summary>Details</summary><p><s>Citizenship in Yoahtl is determined by residency in a Yoahtlan town or on Yoahtlan territory.</s> Citizenship may be granted at the Alcuahtl or Deputy&#39;s discretion on the basis of trust and significant contributions.</p></details></blockquote><p><em>(Superceded via <a href="#council-act-10-5">Council Act 10.5</a>)</em></p><h3 id="council-act-7-1" tabindex="-1">Council Act 7.1 <a class="header-anchor" href="#council-act-7-1" aria-hidden="true">#</a></h3><blockquote><p>Allow recall elections to be advanced by petition of the Ololtic. If the petition receives more than 50% of the submitted votes from the pervious election, and the Chief Justice finds no evidence of collusion or bad faith, and that the given reason is valid, then a new election will be held. Such petitions ought not to be started by other government officials.</p></blockquote><p><em>(Repealed via <a href="#council-act-13-3">Council Act 13.3</a>; Restored via <a href="#council-act-13-8">Council Act 13.8</a>; Amended via Ololtic Proposal 30)</em></p><h3 id="council-act-8-1" tabindex="-1">Council Act 8.1 <a class="header-anchor" href="#council-act-8-1" aria-hidden="true">#</a></h3><blockquote><p>Amend the Charter to state that Council seats are allocated based on the number of voters as defined by the number of players who voted in the election rather than active citizens as is currently stated.</p></blockquote><p><em>(Repealed via <a href="#council-act-13-3">Council Act 13.3</a>; Restored via <a href="#council-act-13.8">Council Act 13.8</a>)</em></p><h3 id="council-act-8-2" tabindex="-1">Council Act 8.2 <a class="header-anchor" href="#council-act-8-2" aria-hidden="true">#</a></h3><blockquote><p><s>Authorize the construction of the Yoahtl Vault.</s></p></blockquote><p><em>(Amended via <a href="#council-act-15-7">Council Act 15.7</a>; Retired via <a href="#council-act-24-2">Council Act 24.2</a>)</em></p><h3 id="council-act-8-3" tabindex="-1">Council Act 8.3 <a class="header-anchor" href="#council-act-8-3" aria-hidden="true">#</a></h3><blockquote><p><s>Make Council Act 8.2 private to the government alone.</s></p></blockquote><p><em>(Repealed via <a href="#council-act-15-7">Council Act 15.7</a>)</em></p><h2 id="decisions-of-the-third-council-december-14th-2017-to-february-14th-2018" tabindex="-1">Decisions of the Third Council - December 14th, 2017 to February 14th, 2018 <a class="header-anchor" href="#decisions-of-the-third-council-december-14th-2017-to-february-14th-2018" aria-hidden="true">#</a></h2><h3 id="council-act-9-1" tabindex="-1">Council Act 9.1 <a class="header-anchor" href="#council-act-9-1" aria-hidden="true">#</a></h3><blockquote><p><s>Amend the charter to add Article III Section 6: Allow recall elections to be advanced by petition of the citizenry. If more than half the total of the previous voters submit a petition for a new election for a council seat and the council finds no evidence of collusion or bad faith then a new election will be held. All petitions must have a reason for recalling the elected official and cannot be initiated or signed by the alcuahtl, his deputy, or other councillors.</s></p></blockquote><p><em>(Repealed via <a href="#council-act-13-3">Council Act 13.3</a>)</em></p><h3 id="council-act-9-2" tabindex="-1">Council Act 9.2 <a class="header-anchor" href="#council-act-9-2" aria-hidden="true">#</a></h3><blockquote><p>The Alcuahtl assigns the role of Auditor-General to an individual Citizen. This duty cannot be delegated. The Auditor-General is responsible for monitoring the supply of government resources (primarily liquid assets and XP). They must account for all transactions within reason, and follow-up and report on any discrepancies. The Auditor-General must ideally not be a Citizen heavily involved in production or expenditure (I.e XP production or defense). The Council may force the Alcuahtl to reconsider their choice of Auditor-General, but may not force a resignation without proof of wrongdoing.</p></blockquote><p>(Amended via <a href="#council-act-13-3">Council Act 13.3</a>; Amended via <a href="#council-act-13-8">Council Act 13.8</a>; Amended via <a href="#council-act-15-6">Council Act 15.6</a>; Amended via <a href="#council-act-24-4">Council Act 24.4</a>)</p><h3 id="council-act-9-3" tabindex="-1">Council Act 9.3 <a class="header-anchor" href="#council-act-9-3" aria-hidden="true">#</a></h3><blockquote><p>Councillors are to make the effort to meet in game when possible during Council meetings.</p></blockquote><h3 id="council-act-9-4" tabindex="-1">Council Act 9.4 <a class="header-anchor" href="#council-act-9-4" aria-hidden="true">#</a></h3><blockquote><p><s>Amend Council Act 5.1:</s></p><details class="details custom-block"><summary>Details</summary><p><s>Property is usage-based, on a first-come, first-served basis - you own what you have built on.</s> <strong>All undeveloped and derelict property is owned by the Government. Plots/land are to be distributed solely by Government members, for free or a fee as deemed necessary. Property transactions between private parties must have prior approval from the Government.</strong></p></details></blockquote><h3 id="council-act-9-5" tabindex="-1">Council Act 9.5 <a class="header-anchor" href="#council-act-9-5" aria-hidden="true">#</a></h3><blockquote><p>The Government and all its property is owned by the public and must act in their ultimate interests.</p></blockquote><h3 id="council-act-9-6" tabindex="-1">Council Act 9.6 <a class="header-anchor" href="#council-act-9-6" aria-hidden="true">#</a></h3><blockquote><p><s>Amend Council Act 1.4:</s></p><details class="details custom-block"><summary>Details</summary><p><s>A protectorate is a city or region designated by the Council and under the protection of Yoahtl. An attack on it is an attack on the Empire of Yoahtl. Citizens of protectorates are considered Yoahtlan residends and ineligible to run for council or vote. A protectorate is required to hold regular elections to elect a Governor to act as the representative of the Yoahtlan State. A governor is considered to be a citizen of Yoahtl and may cast votes on behalf of one-third of the protectorate\u2019s citizens. Protectorates are required to contribute men and materiel during war-time.</s> <strong>The Alcuahtl may appoint a governor to administer the protectorate at their discretion. A vassal is a city or region designated by the Council and under the protection of Yoahtl. An attack on it is an attack on the Empire of Yoahtl. Vassals retain their own governing and legal structure, exempting them from article Article V of the Charter. They must submit to Yoahtlan law in cases involving Yoahtlan citizens, and must adhere to Yoahtlan foreign policy. Vassals are required to contribute men and materiel during war-time. Any group of players wishing to form a town may submit papers of incorporation to the Council of Yoahtl, which are then voted on and the Alcuahtl must sign off. A town may choose to be autonomous, which means that they retain their own legal structure, rights to their land in the event of them going inactive, and government. They also are exempt from participating in warfare unless they choose to contribute. They are, however, unable to vote for the Council and choose their own foreign relations except in the realm of trade. Non-autonomous towns retain the Yoahtlan legal code and can vote for the Council. They also cannot form foreign relations. Existing towns may join Yoahtl as an automatically incorporated town and may choose autonomy at their discretion.</strong></p></details></blockquote><p>(Superceded via Ololtic Proposal 8)</p><h3 id="council-act-9-7" tabindex="-1">Council Act 9.7 <a class="header-anchor" href="#council-act-9-7" aria-hidden="true">#</a></h3><blockquote><p>Repeal <a href="#council-act-6-6">Council Act 6.6</a>.</p></blockquote>',122),n=[i];function l(r,u,s,h,d,p){return o(),t("div",null,n)}var b=e(a,[["render",l]]);export{m as __pageData,b as default};
